import sailpoint.object.context;
  import org.apache.log4j.Logger;
  import org.apache.log4j.Level;
  import sailpoint.tools.Util;

    boolean haschanged = false;
  if(previousIdentity != null && newIdentity != null)
  {
    String Prev_date = previousIdentity.getAttribute("endDate");
    String New_date = newIdentity.getAttribute("endDate");

    if(Util.isNotNullOrEmpty(Prev_date) && Util.isNotNullOrEmpty(New_date))
    {   
      if(!Prev_date.equalsIgnoreCase(New_date))
      {
        haschanged = true;
      }
    }
  }
  return haschanged;
