import sailpoint.object.context;
import org.apache.log4j.Logger;
import org.apache.log4j.Level;

boolean haschanged = false;
String Prev_title = previousIdentity.getAttribute("jobtitle");
String New_title = newIdentity.getAttribute("jobtitle");

String Prev_dept = previousIdentity.getAttribute("department");
String New_dept = newIdentity.getAttribute("department");

if(!Prev_dept.equalsIgnoreCase(New_dept) || !Prev_title.equalsIgnoreCase(New_title))
{
haschanged = true;
}
return haschanged;
